%angular

<button onclick="importNotebooks();">Import Notebooks</button>

<div id="notebooks"></div>

<script type="text/javascript">
function importNotebooks() {
  let notebooks = [
    "DE_Demos_Hive_HiveSkewedTable.json",
    "DE_Demos_Pyspark_ApacheSparkIn5Minutes.json",
    "DE_Demos_Scala_ApacheSparkIn5Minutes.json",
    "DE_Labs_Hive_HivePartitions.json",
    "DE_Labs_Pyspark_CombiningAndSplittingDataFrames.json",
    "DE_Labs_Pyspark_IntroducingDataFrames.json",
    "DE_Labs_Pyspark_ReadingAndWritingDataFrames.json",
    "DE_Labs_Pyspark_SummarizingAndGroupingDataFrames.json",
    "DE_Labs_Pyspark_WorkingWithColumns.json",
    "DE_Labs_Pyspark_WorkingWithComplexTypes.json",
    "DE_Labs_Pyspark_WorkingWithUDFs.json",
    "DE_Labs_Pyspark_WorkingWithWindows.json",
    "DE_Labs_Scala_CombiningAndSplittingDataFrames.json",
    "DE_Labs_Scala_IntroducingDataFrames.json",
    "DE_Labs_Scala_ReadingAndWritingDataFrames.json",
    "DE_Labs_Scala_SummarizingAndGroupingDataFrames.json",
    "DE_Labs_Scala_WorkingWithColumns.json",
    "DE_Labs_Scala_WorkingWithComplexTypes.json",
    "DE_Labs_Scala_WorkingWithUDFs.json",
    "DE_Labs_Scala_WorkingWithWindows.json",
    "DE_Labs_WorkingWithHDFS.json",
    "DE_Labs_WorkingWithYARN.json"
   ];

  let baseUrl = "https://bshimel.github.io/ztest/notebooks/";

  function loadNotebook(note, data) {
    console.log(JSON.stringify(data));
    $.post({ 
      url : "https://knguyen-spark-3-master0.knguyen.kfjr-x0dh.cloudera.site/knguyen-spark-3/cdp-proxy/zeppelin/api/notebook/import",
      data : JSON.stringify(data),
      xhrFields: {
        withCredentials: true
      },
      contentType: "application/json",
      dataType: "json",
      success : function(data, status) {
        $("#notebooks").html($("#notebooks").html() + "\n<p>" + note + "... " + status + "</p>");
        console.log(note + "... " + status);
      },
      error: function(request, error) {
        $("#notebooks").html($("#notebooks").html() + "\n<p>" + note + "... " + error + "</p>");
        console.log(note + "... " + error);
      }
    });
  }


  function getNotebookData(note) {
    $.ajax({
      url : baseUrl + note,
      type : 'GET',
      dataType:'json',
      xhrFields: {
        withCredentials: false
      },
      success : function(data) {
        loadNotebook(note, data);
      },
      error : function(request,error)
      {
          console.log("Request: "+JSON.stringify(request));
      }
    });
  }

  $("#notebooks").html($("#notebooks").html() + "\n<h3>Importing Notebooks</h3>");
  for (let note of notebooks) {
    getNotebookData(note);
  }
}
</script>
